def isValid(S):
    # the seed of a valid string
    seed = 'abc'

    s = S
    while len(s) > 3 and seed in s:
        s = s.replace(seed, '')

    if s == '' or s == 'abc':
        return True
    else:
        return False


print(isValid('abc'))
print(isValid('aabcbc'))
print(isValid('abcabc'))
print(isValid('abcabcababcc'))
print(isValid('abccba'))
print(isValid('ab'))
print(isValid('cababc'))
print(isValid('bac'))
print(isValid('aaabcbcbc'))
print(isValid(''))
